import { useEffect, useState } from "react";
import Header from "../../components/Header";
import * as S from "./style";

const NoticeDatas = [
  {
    img: "https://cdn.imweb.me/upload/S201910012ff964777e0e3/62f9a36ea3cea.jpg",
    title: "강아지 키우는 사람?",
    content: "저 강아지 키우려고 하는데 무슨 종을 키울까요?",
  },
  {
    img: "https://i.namu.wiki/i/nkyyzYnI9mu0oKoO3WshDwLPBRLWPeLAMrqCeTSU9L5mv7AUyWrVKOV_kYsJ0rGCzD6V6w4ox0IMSus8sQ5IBg.webp",
    title: "홀란드 축구 오늘 볼 사람 있나요?",
    content: "축구 같이 봐요!!",
  },
  {
    img: "data:image/jpeg;base64,",
    title: "운동 하는 사람?",
    content: "저랑 같이 헬스 다닐 사람 있나요??",
  },
  {
    img: "data:image/jpeg;base64,",
    title: "저랑 같이 취업 공부해요!!",
    content:
      "안녕하세요 저는 취업을 준비하고 있는 취준생입니다. 저의 목표는 개발자가 되는 것입니다.",
  },
  {
    img: "data:image/jpeg;base64,",
    title: "연애가 하고 싶어요 ..",
    content:
      "안녕하세요 저는 11년차 모솔인 11살 초등학생입니다. 저도 연애를 하고 싶은데 어떻게 해야할까요?",
  },
  {
    img: "https://i.namu.wiki/i/aQzvJirYanTLU_1u6fEeWtwbkULIx_hULiRXa1FbHeiAN6g7b1po5gKsu6CDYz2AVysRmkd9CAwmGA3u6Jcq9w.webp",
    title: "기타",
    content: "저 기타좀 알려줄 사람 있나요?",
  },
];

const ChatPage = () => {
  const [noticeDatas, setNoticeDatas] = useState(NoticeDatas);

  useEffect(() => {
    const ListConfirm = localStorage.getItem("ListConfirm");
    console.log("ListConfirm:", ListConfirm);

    if (ListConfirm === "1") {
      const updatedNoticeDatas = NoticeDatas.filter(
        (data) => data.title !== "홀란드 축구 오늘 볼 사람 있나요?"
      );
      localStorage.setItem("NoticeData", JSON.stringify(updatedNoticeDatas));

      setNoticeDatas(updatedNoticeDatas);
    }
  }, []);

  return (
    <>
      <Header />
      <S.NoticeWrapper>
        <S.NoticeContainer>
          <S.NoticeTitleContainer>
            <S.NoticeTitle>대화 확인</S.NoticeTitle>
          </S.NoticeTitleContainer>
          <S.NoticeItemContainer>
            {noticeDatas.map((data, index) => (
              <S.NoticeItem
                key={index}
                onClick={() => (window.location.href = `/notice/1`)}
              >
                <S.NoticeImg src={data.img} />
                <S.NoticeBar>
                  <S.NoticeMenu>{data.title}</S.NoticeMenu>
                  <S.NoticeContent>
                    {data.content.length > 50
                      ? data.content.substring(0, 50) + "..."
                      : data.content}
                  </S.NoticeContent>
                </S.NoticeBar>
              </S.NoticeItem>
            ))}
          </S.NoticeItemContainer>
        </S.NoticeContainer>
      </S.NoticeWrapper>
    </>
  );
};

export default ChatPage;
